/**
 * Relation Component
 * -------------------------------------
 * Draws a UML-style line from source class to target class.
 */

import React from "react";
import PropTypes from "prop-types";

const Relation = ({
  source,
  target,
  type,
  sourceCardinality,
  targetCardinality,
  classWidth = 200,
  classHeight = 100,
}) => {
  if (!source || !target) return null;

  // Calculate connection points
  const centerX1 = source.x + classWidth / 2;
  const centerY1 = source.y + classHeight / 2;
  const centerX2 = target.x + classWidth / 2;
  const centerY2 = target.y + classHeight / 2;

  // Calculate the angle and distance
  const dx = centerX2 - centerX1;
  const dy = centerY2 - centerY1;
  const angle = Math.atan2(dy, dx);
  const distance = Math.sqrt(dx * dx + dy * dy);

  // If classes overlap, don't render the relation
  if (distance < 10) return null;

  // Calculate edge intersection points
  const start = {
    x: centerX1 + (classWidth / 2) * Math.cos(angle),
    y: centerY1 + (classWidth / 2) * Math.sin(angle)
  };

  const end = {
    x: centerX2 - (classWidth / 2) * Math.cos(angle),
    y: centerY2 - (classWidth / 2) * Math.sin(angle)
  };

  // Generate unique IDs for markers
  const relationId = Math.random().toString(36).substr(2, 9);
  const triangleId = `triangle-${relationId}`;
  const diamondHollowId = `diamond-hollow-${relationId}`;
  const diamondFilledId = `diamond-filled-${relationId}`;
  const arrowId = `arrow-${relationId}`;

  return (
    <svg
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        pointerEvents: "none",
        overflow: "visible",
        zIndex: 0,
      }}
    >
      <defs>
        {/* Triangle marker for generalization */}
        <marker
          id={triangleId}
          viewBox="0 0 10 10"
          refX="9"
          refY="5"
          markerWidth="8"
          markerHeight="8"
          orient="auto"
        >
          <path d="M 0 0 L 10 5 L 0 10 z" fill="white" stroke="black" />
        </marker>

        {/* Diamond marker for aggregation */}
        <marker
          id={diamondHollowId}
          viewBox="0 0 12 12"
          refX="12"
          refY="6"
          markerWidth="10"
          markerHeight="10"
          orient="auto"
        >
          <path d="M 0 6 L 6 0 L 12 6 L 6 12 Z" fill="white" stroke="black" />
        </marker>

        {/* Filled diamond marker for composition */}
        <marker
          id={diamondFilledId}
          viewBox="0 0 12 12"
          refX="12"
          refY="6"
          markerWidth="10"
          markerHeight="10"
          orient="auto"
        >
          <path d="M 0 6 L 6 0 L 12 6 L 6 12 Z" fill="black" stroke="black" />
        </marker>

        {/* Arrow marker for dependency */}
        <marker
          id={arrowId}
          viewBox="0 0 10 10"
          refX="9"
          refY="5"
          markerWidth="8"
          markerHeight="8"
          orient="auto"
        >
          <path d="M 0 0 L 10 5 L 0 10" fill="none" stroke="black" />
        </marker>
      </defs>

      {/* Main line */}
      <line
        x1={start.x}
        y1={start.y}
        x2={end.x}
        y2={end.y}
        stroke="black"
        strokeWidth="2"
        markerStart={
          type === "aggregation"
            ? `url(#${diamondHollowId})`
            : type === "composition"
            ? `url(#${diamondFilledId})`
            : null
        }
        markerEnd={
          type === "generalization"
            ? `url(#${triangleId})`
            : type === "dependency"
            ? `url(#${arrowId})`
            : null
        }
        strokeDasharray={type === "dependency" ? "4,4" : "none"}
      />

      {/* Source Cardinality */}
      {sourceCardinality && (
        <g transform={`translate(${start.x + 20 * Math.cos(angle)},${start.y + 20 * Math.sin(angle)})`}>
          <rect
            x="-15"
            y="-10"
            width="30"
            height="20"
            fill="white"
            stroke="none"
          />
          <text
            textAnchor="middle"
            dominantBaseline="middle"
            fill="black"
            fontSize="12"
          >
            {sourceCardinality}
          </text>
        </g>
      )}

      {/* Target Cardinality */}
      {targetCardinality && (
        <g transform={`translate(${end.x - 20 * Math.cos(angle)},${end.y - 20 * Math.sin(angle)})`}>
          <rect
            x="-15"
            y="-10"
            width="30"
            height="20"
            fill="white"
            stroke="none"
          />
          <text
            textAnchor="middle"
            dominantBaseline="middle"
            fill="black"
            fontSize="12"
          >
            {targetCardinality}
          </text>
        </g>
      )}
    </svg>
  );
};

Relation.propTypes = {
  source: PropTypes.shape({
    x: PropTypes.number,
    y: PropTypes.number,
  }).isRequired,
  target: PropTypes.shape({
    x: PropTypes.number,
    y: PropTypes.number,
  }).isRequired,
  type: PropTypes.oneOf([
    "association",
    "aggregation",
    "composition",
    "generalization",
    "dependency",
  ]).isRequired,
  sourceCardinality: PropTypes.string,
  targetCardinality: PropTypes.string,
  classWidth: PropTypes.number,
  classHeight: PropTypes.number,
};

export default Relation;
