/**
 * Relation Component
 * -------------------------------------
 * Draws a UML-style line from source class to target class.
 */

import React from "react";
import PropTypes from "prop-types";

const Relation = ({
  source,
  target,
  type,
  sourceCardinality,
  targetCardinality,
  classWidth = 200,
  classHeight = 100,
}) => {
  if (!source || !target) return null;

  // Calculate centers and dimensions
  const sourceCenter = {
    x: source.x + classWidth / 2,
    y: source.y + classHeight / 2,
  };

  const targetCenter = {
    x: target.x + classWidth / 2,
    y: target.y + classHeight / 2,
  };

  // Calculate the line vector
  const dx = targetCenter.x - sourceCenter.x;
  const dy = targetCenter.y - sourceCenter.y;
  const angle = Math.atan2(dy, dx);

  // Update the intersection calculation
  const calculateIntersection = (rect, isSource) => {
    const halfWidth = classWidth / 2;
    const halfHeight = classHeight / 2;
    const center = isSource ? sourceCenter : targetCenter;

    // Calculate intersection with rectangle edges
    const m = dy / dx; // Line slope

    // Possible intersection points
    const points = [
      // Right edge
      {
        x: center.x + halfWidth,
        y: center.y + m * halfWidth
      },
      // Left edge
      {
        x: center.x - halfWidth,
        y: center.y - m * halfWidth
      },
      // Top edge
      {
        x: center.x + (-halfHeight / m),
        y: center.y - halfHeight
      },
      // Bottom edge
      {
        x: center.x + (halfHeight / m),
        y: center.y + halfHeight
      }
    ];

    // Find the valid intersection point
    const validPoint = points.find(point => {
      return (
        point.x >= center.x - halfWidth &&
        point.x <= center.x + halfWidth &&
        point.y >= center.y - halfHeight &&
        point.y <= center.y + halfHeight
      );
    });

    return validPoint || { x: center.x, y: center.y };
  };

  const start = calculateIntersection(source, true);
  const end = calculateIntersection(target, false);

  // Generate unique marker IDs
  const markerId = `marker-${source.x}-${source.y}-${target.x}-${target.y}`;

  return (
    <svg
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        pointerEvents: "none",
        overflow: "visible",
        zIndex: 1000,
      }}
    >
      <defs>
        <marker
          id={`${markerId}-triangle`}
          viewBox="0 0 10 10"
          refX="10"
          refY="5"
          markerWidth="10"
          markerHeight="10"
          orient="auto"
        >
          <path d="M 0 0 L 10 5 L 0 10 z" fill="white" stroke="black" />
        </marker>

        <marker
          id={`${markerId}-diamond-hollow`}
          viewBox="0 0 10 10"
          refX="5"
          refY="5"
          markerWidth="10"
          markerHeight="10"
          orient="auto"
        >
          <path d="M 0 5 L 5 0 L 10 5 L 5 10 z" fill="white" stroke="black" />
        </marker>

        <marker
          id={`${markerId}-diamond-filled`}
          viewBox="0 0 10 10"
          refX="5"
          refY="5"
          markerWidth="10"
          markerHeight="10"
          orient="auto"
        >
          <path d="M 0 5 L 5 0 L 10 5 L 5 10 z" fill="black" stroke="black" />
        </marker>
      </defs>

      {/* Main line */}
      <line
        x1={start.x}
        y1={start.y}
        x2={end.x}
        y2={end.y}
        stroke="black"
        strokeWidth="2"
        markerStart={
          type === "aggregation"
            ? `url(#${markerId}-diamond-hollow)`
            : type === "composition"
            ? `url(#${markerId}-diamond-filled)`
            : null
        }
        markerEnd={
          type === "generalization"
            ? `url(#${markerId}-triangle)`
            : type === "dependency"
            ? `url(#${markerId}-triangle)`
            : null
        }
        strokeDasharray={type === "dependency" ? "4,4" : "none"}
      />

      {/* Source Cardinality */}
      {sourceCardinality && (
        <g transform={`translate(${start.x},${start.y})`}>
          <rect
            x="-15"
            y="-20"
            width="30"
            height="16"
            fill="white"
            stroke="none"
          />
          <text
            x="0"
            y="-10"
            textAnchor="middle"
            dominantBaseline="middle"
            fontSize="12"
          >
            {sourceCardinality}
          </text>
        </g>
      )}

      {/* Target Cardinality */}
      {targetCardinality && (
        <g transform={`translate(${end.x},${end.y})`}>
          <rect
            x="-15"
            y="-20"
            width="30"
            height="16"
            fill="white"
            stroke="none"
          />
          <text
            x="0"
            y="-10"
            textAnchor="middle"
            dominantBaseline="middle"
            fontSize="12"
          >
            {targetCardinality}
          </text>
        </g>
      )}
    </svg>
  );
};

Relation.propTypes = {
  source: PropTypes.shape({
    x: PropTypes.number,
    y: PropTypes.number,
  }).isRequired,
  target: PropTypes.shape({
    x: PropTypes.number,
    y: PropTypes.number,
  }).isRequired,
  type: PropTypes.oneOf([
    "association",
    "aggregation",
    "composition",
    "generalization",
    "dependency",
  ]).isRequired,
  sourceCardinality: PropTypes.string,
  targetCardinality: PropTypes.string,
  classWidth: PropTypes.number,
  classHeight: PropTypes.number,
};

export default Relation;
