/**
 * Sidebar Component
 * -------------------------------------
 * Displays navigation and lists of existing Classes, Relations, and Groups.
 * Contains undo/redo buttons, as well as a reset button for clearing the diagram.
 */

import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { resetDiagram, undo, redo } from "../../redux/editorSlice";
import {
  Button,
  List,
  ListItem,
  ListItemText,
  Typography,
  Divider,
  Box,
  IconButton,
  Tooltip,
  Collapse,
} from "@mui/material";

import UndoIcon from "@mui/icons-material/Undo";
import RedoIcon from "@mui/icons-material/Redo";
import RefreshIcon from "@mui/icons-material/Refresh";
import GroupWorkIcon from "@mui/icons-material/GroupWork";
import ClassIcon from "@mui/icons-material/Class";
import LinkIcon from "@mui/icons-material/Link";
import ExpandLess from "@mui/icons-material/ExpandLess";
import ExpandMore from "@mui/icons-material/ExpandMore";

/**
 * Helper to get class name from ID
 * @param {string} id
 * @param {Array} classes
 * @returns {string} class name or 'Unknown'
 */
const getClassName = (id, classes) => {
  const cls = classes.find((c) => c.id === id);
  return cls ? cls.name : "Unknown";
};

/**
 * Helper to capitalize a string's first letter
 * @param {string} s
 * @returns {string}
 */
const capitalize = (s) => {
  if (typeof s !== "string") return "";
  return s.charAt(0).toUpperCase() + s.slice(1);
};

const Sidebar = () => {
  const dispatch = useDispatch();
  const classes = useSelector((state) => state.editor.classes);
  const relations = useSelector((state) => state.editor.relations);
  const groups = useSelector((state) => state.editor.groups);

  // For toggling open/close of collapses
  const [openClasses, setOpenClasses] = useState(true);
  const [openRelations, setOpenRelations] = useState(true);
  const [openGroups, setOpenGroups] = useState(true);

  /**
   * Resets the entire diagram state
   */
  const handleResetDiagram = () => {
    if (
      window.confirm(
        "Êtes-vous sûr de vouloir réinitialiser le diagramme ? Cette action est irréversible."
      )
    ) {
      dispatch(resetDiagram());
    }
  };

  /**
   * Undo the last change
   */
  const handleUndo = () => {
    dispatch(undo());
  };

  /**
   * Redo the last undone change
   */
  const handleRedo = () => {
    dispatch(redo());
  };

  return (
    <Box
      sx={{
        width: 300,
        padding: 2,
        overflowY: "auto",
        height: "100%",
        backgroundColor: "background.paper",
        borderRight: "1px solid #ddd",
      }}
    >
      <Typography variant="h5" gutterBottom>
        Menu Principal
      </Typography>
      <Divider sx={{ marginY: 2 }} />

      {/* Undo/Redo Buttons */}
      <Box sx={{ display: "flex", gap: 1, mb: 2 }}>
        <Tooltip title="Annuler (Ctrl+Z)">
          <IconButton onClick={handleUndo} color="primary">
            <UndoIcon />
          </IconButton>
        </Tooltip>
        <Tooltip title="Rétablir (Ctrl+Y)">
          <IconButton onClick={handleRedo} color="primary">
            <RedoIcon />
          </IconButton>
        </Tooltip>
      </Box>

      {/* Classes List */}
      <List>
        <ListItem button onClick={() => setOpenClasses(!openClasses)}>
          <ClassIcon fontSize="small" sx={{ marginRight: 1 }} />
          <ListItemText primary="Classes" />
          {openClasses ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={openClasses} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            {classes.length > 0 ? (
              classes.map((cls) => (
                <ListItem key={cls.id} sx={{ pl: 4 }}>
                  <ListItemText primary={cls.name} />
                </ListItem>
              ))
            ) : (
              <ListItem sx={{ pl: 4 }}>
                <ListItemText
                  primary={
                    <Typography variant="body2" color="textSecondary">
                      Aucune classe ajoutée
                    </Typography>
                  }
                />
              </ListItem>
            )}
          </List>
        </Collapse>
      </List>

      <Divider sx={{ marginY: 2 }} />

      {/* Relations List */}
      <List>
        <ListItem button onClick={() => setOpenRelations(!openRelations)}>
          <LinkIcon fontSize="small" sx={{ marginRight: 1 }} />
          <ListItemText primary="Relations" />
          {openRelations ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={openRelations} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            {relations.length > 0 ? (
              relations.map((rel) => (
                <ListItem key={rel.id} sx={{ pl: 4 }}>
                  <ListItemText
                    primary={`${getClassName(rel.source, classes)} → ${getClassName(
                      rel.target,
                      classes
                    )} (${capitalize(rel.type)})`}
                  />
                </ListItem>
              ))
            ) : (
              <ListItem sx={{ pl: 4 }}>
                <ListItemText
                  primary={
                    <Typography variant="body2" color="textSecondary">
                      Aucune relation ajoutée
                    </Typography>
                  }
                />
              </ListItem>
            )}
          </List>
        </Collapse>
      </List>

      <Divider sx={{ marginY: 2 }} />

      {/* Groups List */}
      <List>
        <ListItem button onClick={() => setOpenGroups(!openGroups)}>
          <GroupWorkIcon fontSize="small" sx={{ marginRight: 1 }} />
          <ListItemText primary="Groupes" />
          {openGroups ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={openGroups} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            {groups.length > 0 ? (
              groups.map((group) => (
                <ListItem key={group.id} sx={{ pl: 4 }}>
                  <ListItemText primary={group.name} />
                </ListItem>
              ))
            ) : (
              <ListItem sx={{ pl: 4 }}>
                <ListItemText
                  primary={
                    <Typography variant="body2" color="textSecondary">
                      Aucun groupe ajouté
                    </Typography>
                  }
                />
              </ListItem>
            )}
          </List>
        </Collapse>
      </List>

      <Divider sx={{ marginY: 3 }} />

      {/* Reset Diagram Button */}
      <Button
        variant="contained"
        color="error"
        onClick={handleResetDiagram}
        startIcon={<RefreshIcon />}
        fullWidth
        sx={{ mb: 2 }}
      >
        Réinitialiser Diagramme
      </Button>
    </Box>
  );
};

export default Sidebar;
